const DISPLAY=document.getElementById("display"),CLEAR=document.getElementById("clear"),EQUALS=document.getElementById("equals"),MEM_RECALL=document.getElementById("mem-recall"),MEM_SAVE=document.getElementById("mem-save"),MEM_CLEAR=document.getElementById("mem-clear");let currentDisplay=DISPLAY.innerText,firstNumber,secondNumber,storedOperator="";window.onload=()=>{DISPLAY.innerText="0"},document.addEventListener("keydown",event=>{switch(event.key){case"0":showInputOnDisplay(0);break;case"1":showInputOnDisplay(1);break;case"2":showInputOnDisplay(2);break;case"3":showInputOnDisplay(3);break;case"4":showInputOnDisplay(4);break;case"5":showInputOnDisplay(5);break;case"6":showInputOnDisplay(6);break;case"7":showInputOnDisplay(7);break;case"8":showInputOnDisplay(8);break;case"9":showInputOnDisplay(9);break;case".":showInputOnDisplay(".");break;case"Escape":clearDisplay();break;case"/":operatorChosen("/");break;case"*":operatorChosen("*");break;case"-":operatorChosen("-");break;case"+":operatorChosen("+");break;case"Enter":performCalculation()}}),CLEAR.addEventListener("click",clearDisplay),EQUALS.addEventListener("click",performCalculation),MEM_RECALL.addEventListener("click",()=>{numberRetreived=localStorage.getItem("stored number"),DISPLAY.innerText=numberRetreived}),MEM_SAVE.addEventListener("click",()=>{let numberStored=DISPLAY.innerText;localStorage.setItem("stored number",numberStored),clearDisplay()}),MEM_CLEAR.addEventListener("click",()=>{localStorage.clear()});const buttons=document.querySelectorAll(".inputs");for(const button of buttons)button.addEventListener("click",()=>{"."===button.value&&-1!==currentDisplay.indexOf(".")||showInputOnDisplay(button.value)});const operators=document.querySelectorAll(".operator");for(const operator of operators)operator.addEventListener("click",()=>{operatorChosen(operator.value)});function showInputOnDisplay(number){return"0"===currentDisplay&&(currentDisplay=""),currentDisplay+=number,DISPLAY.innerText=currentDisplay}function operatorChosen(operator){firstNumber=DISPLAY.innerText,storedOperator=operator,clearDisplay()}function performCalculation(){let calculation=0;switch(secondNumber=DISPLAY.innerText,storedOperator){case"/":calculation=parseFloat(firstNumber)/parseFloat(secondNumber);break;case"*":calculation=parseFloat(firstNumber)*parseFloat(secondNumber);break;case"-":calculation=parseFloat(firstNumber)-parseFloat(secondNumber);break;case"+":calculation=parseFloat(firstNumber)+parseFloat(secondNumber)}DISPLAY.innerText=calculation.toLocaleString()}function clearDisplay(){currentDisplay="0",DISPLAY.innerText=currentDisplay}