window.onload=()=>{document.getElementById("display").innerText="0"};let currentDisplay=DISPLAY.innerText,firstNumber,secondNumber,storedOperator="";const DISPLAY=document.getElementById("display"),CLEAR=document.getElementById("clear"),EQUALS=document.getElementById("equals"),MEM_RECALL=document.getElementById("mem-recall"),MEM_SAVE=document.getElementById("mem-save"),MEM_CLEAR=document.getElementById("mem-clear");document.addEventListener("keydown",event=>{switch(event.which){case 96:showInputOnDisplay(0);break;case 97:showInputOnDisplay(1);break;case 98:showInputOnDisplay(2);break;case 99:showInputOnDisplay(3);break;case 100:showInputOnDisplay(4);break;case 101:showInputOnDisplay(5);break;case 102:showInputOnDisplay(6);break;case 103:showInputOnDisplay(7);break;case 104:showInputOnDisplay(8);break;case 105:showInputOnDisplay(9);break;case 110:showInputOnDisplay(".");break;case 27:clearDisplay();break;case 111:operatorChosen("/");break;case 106:operatorChosen("*");break;case 109:operatorChosen("-");break;case 107:operatorChosen("+");break;case 13:performCalculation()}}),CLEAR.addEventListener("click",clearDisplay),EQUALS.addEventListener("click",performCalculation),MEM_RECALL.addEventListener("click",()=>{numberRetreived=localStorage.getItem("stored number"),DISPLAY.innerText=numberRetreived}),MEM_SAVE.addEventListener("click",()=>{let numberStored=DISPLAY.innerText;localStorage.setItem("stored number",numberStored),clearDisplay()}),MEM_CLEAR.addEventListener("click",()=>{localStorage.clear()});const buttons=document.querySelectorAll(".inputs");for(const button of buttons)button.addEventListener("click",()=>{"."===button.value&&-1!==currentDisplay.indexOf(".")||showInputOnDisplay(button.value)});const operators=document.querySelectorAll(".operator");for(const operator of operators)operator.addEventListener("click",()=>{operatorChosen(operator.value)});function showInputOnDisplay(number){return"0"===currentDisplay&&(currentDisplay=""),currentDisplay+=number,DISPLAY.innerText=currentDisplay}function operatorChosen(operator){firstNumber=DISPLAY.innerText,storedOperator=operator,clearDisplay()}function performCalculation(){let calculation=0;switch(secondNumber=DISPLAY.innerText,storedOperator){case"/":calculation=parseFloat(firstNumber)/parseFloat(secondNumber);break;case"*":calculation=parseFloat(firstNumber)*parseFloat(secondNumber);break;case"-":calculation=parseFloat(firstNumber)-parseFloat(secondNumber);break;case"+":calculation=parseFloat(firstNumber)+parseFloat(secondNumber)}DISPLAY.innerText=calculation.toLocaleString()}function clearDisplay(){currentDisplay="0",DISPLAY.innerText=currentDisplay}