window.onload=()=>{document.getElementById("display").innerText="0"},document.addEventListener("keydown",event=>{switch(event.key){case 96:showInputOnDisplay(0);break;case 97:showInputOnDisplay(1);break;case 98:showInputOnDisplay(2);break;case 99:showInputOnDisplay(3);break;case 100:showInputOnDisplay(4);break;case 101:showInputOnDisplay(5);break;case 102:showInputOnDisplay(6);break;case 103:showInputOnDisplay(7);break;case 104:showInputOnDisplay(8);break;case 105:showInputOnDisplay(9);break;case 110:showInputOnDisplay(".");break;case 27:clearDisplay();break;case 111:operatorChosen("/");break;case 106:operatorChosen("*");break;case 109:operatorChosen("-");break;case 107:operatorChosen("+");break;case 13:performCalculation()}}),document.getElementById("clear").addEventListener("click",clearDisplay),document.getElementById("equals").addEventListener("click",performCalculation),document.getElementById("mem-recall").addEventListener("click",()=>{numberRetreived=localStorage.getItem("stored number"),document.getElementById("display").innerText=numberRetreived}),document.getElementById("mem-save").addEventListener("click",()=>{let numberStored=document.getElementById("display").innerText;localStorage.setItem("stored number",numberStored),clearDisplay()}),document.getElementById("mem-clear").addEventListener("click",()=>{localStorage.clear()});let currentDisplay=document.getElementById("display").innerText,firstNumber,secondNumber,storedOperator="";const buttons=document.querySelectorAll(".inputs");for(const button of buttons)button.addEventListener("click",()=>{"."===button.value&&-1!==currentDisplay.indexOf(".")||showInputOnDisplay(button.value)});const operators=document.querySelectorAll(".operator");for(const operator of operators)operator.addEventListener("click",()=>{operatorChosen(operator.value)});function showInputOnDisplay(number){return"0"===currentDisplay&&(currentDisplay=""),currentDisplay+=number,document.getElementById("display").innerText=currentDisplay}function operatorChosen(operator){firstNumber=document.getElementById("display").innerText,storedOperator=operator,clearDisplay()}function performCalculation(){let calculation=0;switch(secondNumber=document.getElementById("display").innerText,storedOperator){case"/":calculation=parseFloat(firstNumber)/parseFloat(secondNumber);break;case"*":calculation=parseFloat(firstNumber)*parseFloat(secondNumber);break;case"-":calculation=parseFloat(firstNumber)-parseFloat(secondNumber);break;case"+":calculation=parseFloat(firstNumber)+parseFloat(secondNumber)}document.getElementById("display").innerText=calculation.toLocaleString()}function clearDisplay(){currentDisplay="0",document.getElementById("display").innerText=currentDisplay}